// mysql.gateway.js
const mysql = require('mysql');
const { Pool } = require('mysql');

class MysqlGateway {
  constructor(config) {
    this.config = config;
    this.pool = mysql.createPool(config);
  }

  async query(sql, params) {
    return new Promise((resolve, reject) => {
      this.pool.query(sql, params, (err, results) => {
        if (err) {
          reject(err);
        } else {
          resolve(results);
        }
      });
    });
  }
}

module.exports = MysqlGateway;
```
** Adapters **
```
// jwtAdapter.js
const jwt = require('jsonwebtoken');

class JwtAdapter {
  constructor(config) {
    this.config = config;
  }

  generateToken(user) {
    return jwt.sign({ id: user.id, username: user.username }, this.config.secret, {
      expiresIn: this.config.expiresIn,
    });
  }

  verifyToken(token) {
    try {
      return jwt.verify(token, this.config.secret);
    } catch (err) {
      return null;
    }
  }
}

module.exports = JwtAdapter;
```
** Framework **
