// express.framework.js
const express = require('express');
const app = express();
const jwtMiddleware = require('./jwtMiddleware');

app.use(express.json());

app.post('/login', jwtMiddleware, async (req, res) => {
  const { username, password } = req.body;
  const loginUseCase = new LoginUseCase(new UserRepository(new MysqlGateway(config)));
  try {
    const user = await loginUseCase.execute(username, password);
    const token = new JwtAdapter(config).generateToken(user);
    res.json({ token });
  } catch (err) {
    res.status(401).json({ error: 'Invalid username or password' });
  }
});

app.get('/protected', jwtMiddleware, async (req, res) => {
  res.json({ message: 'Hello, protected route!' });
});

module.exports = app;
```
** JWT Middleware **
```
// jwtMiddleware.js
const jwtAdapter = new JwtAdapter(config);

module.exports = async (req, res, next) => {
  const token = req.headers['x-access-token'] || req.headers['authorization'];
  if (!token) {
    return res.status(401).json({ error: 'Unauthorized' });
  }
  const decoded = jwtAdapter.verifyToken(token);
  if (!decoded) {
    return res.status(401).json({ error: 'Invalid token' });
  }
  req.user = decoded;
  next();
};
```
